/**
 * PairBarriers
 * @constructor
 *
 * five.PairBarriers();
 *
 * five.PairBarriers({
 *   pin: 10
 * });
 *
 *
 * @param {Object} opts [description]
 *
 */

function PairBarriers(extPin, intPin) {
    var externalBarrier =  new this.Barrier(extPin);
    var internalBarrier =  new this.Barrier(intPin);
    var combination = [false,false];

    externalBarrier.on('up',function(){
        combination[0] = false;
        console.log(combination);
    });

    externalBarrier.on('down',function(){
        combination[0] = true;
        console.log(combination);
    });

    internalBarrier.on('up',function(){
        combination[1] = false;
        console.log(combination);
    });

    internalBarrier.on('down',function(){
        combination[1] = true;
        console.log(combination);
    });
};

module.exports = PairBarriers;
